					$affectedRows += $this->aFormulario->save($con);
				}
				$this->setFormulario($this->aFormulario);
			}

			if ($this->aCampo !== null) {
				if ($this->aCampo->isModified() || $this->aCampo->isNew()) {
					$affectedRows += $this->aCampo->save($con);
				}
				$this->setCampo($this->aCampo);
			}

			if ($this->aUsuario !== null) {
				if ($this->aUsuario->isModified() || $this->aUsuario->isNew()) {
					$affectedRows += $this->aUsuario->save($con);
				}
				$this->setUsuario($this->aUsuario);
			}

			if ($this->aParametro !== null) {
				if ($this->aParametro->isModified() || $this->aParametro->isNew()) {
					$affectedRows += $this->aParametro->save($con);
				}
				$this->setParametro($this->aParametro);
			}

			if ($this->aEmpresa !== null) {
				if ($this->aEmpresa->isModified() || $this->aEmpresa->isNew()) {
					$affectedRows += $this->aEmpresa->save($con);
				}
				$this->setEmpresa($this->aEmpresa);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = TareaPeer::ID_TAREA;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
				if ($this->isNew()) {
					$pk = TareaPeer::doInsert($this, $con);
					$affectedRows += 1; // we are assuming that there is only 1 row per doInsert() which
										 // should always be true here (even though technically
										 // BasePeer::doInsert() can insert multiple rows).

