	 * @return     mixed <code>true</code> if all validations pass; array of <code>ValidationFailed</code> objets otherwise.
	 */
	protected function doValidate($columns = null)
	{
		if (!$this->alreadyInValidation) {
			$this->alreadyInValidation = true;
			$retval = null;

			$failureMap = array();


			// We call the validate method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aFormulario !== null) {
				if (!$this->aFormulario->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aFormulario->getValidationFailures());
				}
			}

			if ($this->aCampo !== null) {
				if (!$this->aCampo->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aCampo->getValidationFailures());
				}
			}

			if ($this->aUsuario !== null) {
				if (!$this->aUsuario->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aUsuario->getValidationFailures());
				}
			}

			if ($this->aParametro !== null) {
				if (!$this->aParametro->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aParametro->getValidationFailures());
				}
			}

			if ($this->aEmpresa !== null) {
				if (!$this->aEmpresa->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aEmpresa->getValidationFailures());
				}
			}


			if (($retval = TareaPeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}



			$this->alreadyInValidation = false;
		}

		return (!empty($failureMap) ? $failureMap : true);
	}

